import React, { useState,  useEffect} from "react";
import Bandeau from "../components/Bandeau.jsx";
import Carte from "../components/Carte.jsx";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const Home = () => {
const [cardAnimaux, setCardAnimaux] = useState([]);

useEffect(() => {
   axios
   .get(`http://localhost:5005/animal`)
   .then((res) => {
    setCardAnimaux(res.data);
   })
   .catch((err) => {
     console.error(err);
  });
}, []);

    return (
        <div className='home'>
            <div className="bando">
            <Bandeau />
            </div>
        <div className="leTout">
                <div className="cadre">
                    <div className="titre">TROUVE TON AmisDOm</div>
                    <div className="filtre">filtre</div>
                </div>
                    <div className='toCarte'>
                        {cardAnimaux.map((val) => {
                            return (
                                <Link to="/Descriptif/${val.id_animaux" className="Link"key={val.id} >

                                    <Carte profil={val}/>
                                </Link>
                                    );
                        })}         
                    </div>
        </div>
        </div>
        )}


export default Home;
