import React, { useState,  useEffect} from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "../styles/Carte.css";
import lapin from "../assets/lapin.jpg";

const Carte = () => {

   const [profil, setProfil] = useState([]);

   useEffect(() => {
    axios
      .get(`http://localhost:${import.meta.env.VITE_PORT_BACK}/animaux`)
      .then((res) => {
        setProfil(res.data);
      })
      .catch((err) => {
        console.error(err);
      });
  }, []);

    return (
        <div className='carte'>
            <Link to="/Description"></Link>
             <div className="imga">
                    {profil.map((el) => {
                        return (
                        <div className="descri" key={el.id}>
                        <img src={`http://localhost:${import.meta.env.VITE_PORT_BACK}/assets/${el.image}`}alt="tete"/>
                        <div className="name">{el.name}</div>
                        <div className="gabarit">{el.gabarit}{el.type}</div>
                        <div className="age">{el.age}</div>
                        </div>
                        );
                    })}
                </div>
              );
              </div>
)}

export default Carte;
