import React from 'react';
import Bandeau from "../components/Bandeau.jsx";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const home = () => {
    const [cardAnimaux, setCardAnimaux] = useState([]);

const cardanimaux = () => {
    axios
    .get(`http://localhost:${import.meta.env.VITE_PORT_BACK}/tutorial`)
    .then((res) => {
      setCardAnimaux(res.data);
    })
    .catch((err) => {
      console.error(err);
    });
};

useEffect(() => {
  cardanimaux();
}, []);

    return (
        <div className='home'>
            <Bandeau />
           <div className='touteCarte'>
           {cardAnimaux.map((val) => {
              return (
                <Link to={`/description/${val.id}`} className="Link" key={val.id}>
                  <div className="LeTuto">
                    <div className="Sujet">
                      <h2>{val.title}</h2>
                    </div>
                  </div>
                </Link>
              );
            })}
            </div>
        </div>
    );
};

export default home;
