const Descriptif = () => {
const [descrip, setDescrip] = useState([]);

    useEffect(() => {
    axios
      .get(`http://localhost:5005/animal`)
      .then((res) => {
       setDescrip(res.data);
      })
      .catch((err) => {
       console.error(err);
      });
 }, []);

    return (
        <div className='descriptif'>
            <div className='bande'>
            <Bandeau />
            </div>
            <div className='presentation'>
                <div className='qui'>
                    <p>Je te pr√©sente</p>

                <div className="test">
                {descrip.map((val) => {
                    return (
                <div className='tofNom' key={val.id_animaux}>
                    <img className="pin" src={`http://localhost:${process.env.REACT_APP_PORT}/${val.image}`} alt="lapin"/>
                        <div className='info'>
                        <div className="name">{val.name}</div>
                        <div className="gabarit">{val.gabarit}</div>
                        <div className="type">{val.type}</div>
                        <div className="age">{val.age} ans</div>
                        </div>
                    <div className='text'>{val.descriptif}</div>
                </div>
                );
            })}
            </div>
        </div>
        </div>
        </div>

)}

export default Descriptif;
