import React, { useState,  useEffect} from "react";
import {useParams} from 'react-router-dom'
import axios from "axios";
import Bandeau from "../components/Bandeau.jsx";
import "../styles/Descriptif.css";

const Descriptif = () => {
const [descrip, setDescrip] = useState([]);
const [delAnimaux, setDelAnimaux] = useState("");
const [targetId, setTargetId] = useState(null);
const {id} = useParams();

const deleteanimaux = () => {
    axios
      .delete(`http://localhost:5005/animal/${targetId}`)
      .then(() => {
        setDelAnimaux("Suppression réussie !");
      })
      .catch(() => {
        setDelAnimaux("Suppression échouée !");
      });
  };

    useEffect(() => {
    axios
      .get(`http://localhost:5005/animal/${id}`)
      .then((res) => {
       setDescrip(res.data);
      })
      .catch((err) => {
       console.error(err);
      });
 }, []);

    return (
        <div className='cadreDescriptif'>
            <div className="bando">
            <Bandeau />
            </div>
            <div className='presentation'>
                <div className="titrePres">
                    <p>Je te présente</p>
                    </div>
            <div className="test">
                {descrip
                .map((val) => {
                    return (
                <div className='rass' key={val.id}>
                    <div className="tofCarre">
                    <img className="pin" src={`http://localhost:5005/${val.image}`} alt="pin"/>
                    </div>
                        <div className='info'>
                            <div className="name">{val.name}</div>
                            <div className="sexe">{val.sexe}</div>
                            <div className="type">{val.type}</div>
                            <div className="age">{val.age} ans</div>
                            <div className="texte">
                                <div className='text'>{val.descriptif}</div>
                            </div>
                    </div>
                </div>
                );
                })}
            </div>
            <button
        className="adopte"
        type="submit"
        onClick={() => {
            deleteanimaux();
        }}
      >
        adopte
      </button>
        </div>
        </div>
)}

export default Descriptif;
