import Bandeau from "../components/Bandeau.jsx";
import Search from "../components/Search.jsx";
import Carte from "../components/Carte.jsx";
import axios from "axios";
import { Link } from "react-router-dom";
import "../styles/Home.css";

const Home = () => {
const [cardAnimaux, setCardAnimaux] = useState([]);
const [searchType, setSearchType] = useState("");
const [type, setType] = useState();

useEffect(() => {
   axios
   .get(`http://localhost:5005/animal`)
   .then((res) => {
    setCardAnimaux(res.data);
   })
   .catch((err) => {
     console.error(err);
  });
}, []);

const handleSearchTerm = (e) => {
    setSearchType(e.target.value)
 };

const getType = () => {
    axios
   .get(`http://localhost:5005/animal`)
   .then((res) => {
    setCardAnimaux(res.data);
   })
   .catch((err) => {
     console.error(err);
  });
}
    return (
        <div className='home'>
            <div className="bando">
            <Bandeau />
            </div>
        <div className="leTout">
                <div className="cadre">
                    <div className="titre">TROUVE TON AmisDOm</div>
                    <Search datas={cardAnimaux} handleSearchTerm= {handleSearchTerm} />
                </div>
                    <div className='toCarte'>
                        {cardAnimaux
                        .filter((el) => {
                            if (searchType === "")
                            return true;
                            if (searchType === el.type)
                            return true;
                        }

                        )
                        .map((val) => {
                            return (
                                <Link to={`/Descriptif/${val.id_animaux}`} className="Link"key={val.id_animaux} >

                                    <Carte profil={val}/>

                                </Link>
                                    );
                        })}         
                    </div>
        </div>
        </div>
        )}


export default Home;
