        <div className='carte'>

             <div className="imga">
                <img className="tete" src={el.image} alt="lapin"/>
                <Link to="/Descriptif">
                    {profil.map((el) => (
                        <div className="descri">
                        <div className="name">{el.name}</div>
                        <div className="gabarit">{el.gabarit}{el.type}</div>
                        <div className="age">{el.image}</div>
                    </div>
                    ))}

                    </Link>
            </div>
        </div>
    );
};

export default Carte;
